//        console.log(data)
//        setName(data.Name)
//        setCode(data.Code)
//      })
//      .catch(error => console.error(error))

  }

  return (<div>
            <h1>SpringBoot Backend Testing:</h1>          
            <hr/>
            <br/>
            <h3> Get a Single Country's Data:</h3>
            <form>
              <label>
                Enter a VALID Country Code:
                <input type="text" name="name" />
              </label>
              <input type="submit" value="Submit" />
            </form>
            <CButton onClick={onButton}> Get Backend Data</CButton>
            <h5> Name: {name} </h5>
            <h5> Code: {code} </h5>

          </div>)

}

export default TestPage;
